import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import Login from './Login';
import Register from './Register';
import Nav from './Nav';
import EventPage from './EventPage';
import { UserProvider } from './UserContext';
import NewEvent from './NewEvent';
import PastEvents from './PastEvents';
import OrderPage from './OrderPage';
import HomePage from './MainPage';

function App() {
  const [cart, setCart] = useState([]);
  const addToCart = (eventData) => {
    setCart([...cart, eventData]);
    console.log('Added to Cart:', eventData);
    // Add logic to update the cart state or perform other actions
  };

  const unpurchasedCount = cart.length;

  return (
    <Router>
      <UserProvider>
        {/* Include Nav component outside Routes to make it visible on all pages */}
        <Nav unpurchasedCount={unpurchasedCount} />

        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/eventpage" element={<EventPage addToCart={addToCart} unpurchasedCount={unpurchasedCount} />} />
          <Route path="/pastevents" element={<PastEvents />} />
          <Route path="/newevent" element={<NewEvent />} />
          <Route path="/orders" element={<OrderPage />} />
          <Route path="/mainpage" element={<HomePage />} />
        </Routes>
      </UserProvider>
    </Router>
  );
}

export default App;
